])
function handleCollection(e: MouseEvent) {
    const target = e.target as HTMLElement;
    const collectionClick = target.closest('.star2');
    if (!collectionClick) return
    const movieId = collectionClick.getAttribute('data-id');

    if (!moviesArr.value[Number(movieId) - 1].is_collected) {
        moviesArr.value[Number(movieId) - 1].is_collected = true
        ElMessage({
            message: '电影收藏成功',
            type: 'success',
        });
    } else {
        moviesArr.value[Number(movieId) - 1].is_collected = false
        ElMessage({
            message: '取消电影收藏',
            type: 'success',
        });
    }

}
</script>
